import "./LastReviews.css";

function LastReviews({ allReviews, allMovies }) {
  console.log(allMovies);
  return (
    <section className="last-reviews-container">
      <hr />
      <h2>Last reviews</h2>
      <ul>
        {allReviews.map((review) => {
          console.log(review.imdbId);
          console.log(allMovies);
          const movieData = allMovies?.find(
            (movie) => movie.imdbId === review.imdbId
          );
          return (
            <Review
              key={review.id.timestamp}
              review={review}
              movieData={movieData}
            />
          );
        })}
      </ul>
    </section>
  );
}

function Review({ review, movieData }) {
  const timestamp = review.id.timestamp;
  const fecha = new Date(timestamp * 1000);
  const fechaFormateada = fecha.toLocaleString();
  return (
    <>
      <li className="list-group-item">
        <div className="d-flex justify-content-between align-items-center">
          <span>{review.body}</span>
          <span>{fechaFormateada}</span>
          <figure>
            <img src={movieData.poster} alt="" srcset="" />
            <figcaption>{movieData.title}</figcaption>
          </figure>

          <span className="badge bg-secondary">{review.username}</span>
        </div>
      </li>
      <hr />
    </>
  );
}

export default LastReviews;
