import React, { useState } from "react";
import Button from "react-bootstrap/Button";

const generos = {
  genres: [
    "Action",
    "Adventure",
    "Comedy",
    "Drama",
    "Fantasy",
    "Horror",
    "Mystery",
    "Romance",
    "Sci-Fi",
    "Thriller",
  ],
};

const GenerosListado = ({ generos }) => {
  const [generoSeleccionado, setGeneroSeleccionado] = useState(null);

  const handleGeneroClick = (genero) => {
    setGeneroSeleccionado(genero);
    //peliculasPorGenero(genero);
  };

  return (
    <div>
      <h2>Listado de GÃ©neros</h2>
      <div className="btn-group">
        {generos.map((genero) => (
          <Button
            key={genero.id}
            variant={genero === generoSeleccionado ? "primary" : "secondary"}
            onClick={() => handleGeneroClick(genero)}
          >
            {genero.nombre}
          </Button>
        ))}
      </div>
    </div>
  );
};

export default GenerosListado;
